mapper.insert:

			insert into customer (customer_no, customer_id, customer_password, customer_name, customer_role)
				values(
    				SEQ_CUSTOMER_NO.nextval
   					 ,#{customerId}
   					 ,#{customerPassword}
    				 ,#{customerName}
    				 ,1
    				)

mapper.select:

  SELECT customer_name AS customerName
	FROM customer
  WHERE customer_id = #{customerId}
