mapper.select:
SELECT COUNT(*) FROM product WHERE 1 = 1
AND price BETWEEN #{minPrice} AND #{maxPrice}
AND product_no = #{keyword}
AND product_name like '%'||#{keyword}||'%'
AND category = '${category}'

SELECT d.product_no as productNo, d.product_name as productName, d.product_ea as productEa, d.price, d.product_content as productContent, d.reg_date as regDate, d.category, d.subcategory as subCategory, d.save_name as saveName, d.image_no as imageNo FROM(SELECT rownum as rn, c.product_no, c.product_name, c.product_ea, c.price, c.product_content, c.reg_date, c.category, c.subcategory , c.save_name , c.image_no FROM (SELECT a.product_no, a.product_name, a.product_ea, a.price, a.product_content, a.reg_date, a.category, a.subcategory , b.save_name , b.image_no FROM product a INNER JOIN product_image b ON a.product_no = b.product_no WHERE 1 = 1
AND price BETWEEN #{minPrice} AND #{maxPrice}
AND a.product_no = #{keyword}
AND product_name like '%'||#{keyword}||'%'
AND category = '${category}'
ORDER BY
a.price ,
a.product_no DESC) c ) d WHERE d.rn > (#{pageNum}-1) * #{amount} AND d.rn <= #{pageNum} * #{amount}

SELECT d.product_no as productNo, d.product_name as productName, d.product_ea as productEa, d.price, d.product_content as productContent, d.reg_date as regDate, d.category, d.subcategory as subCategory, d.save_name as saveName, d.image_no as imageNo FROM(SELECT rownum as rn, c.product_no, c.product_name, c.product_ea, c.price, c.product_content, c.reg_date, c.category, c.subcategory , c.save_name , c.image_no FROM (SELECT a.product_no, a.product_name, a.product_ea, a.price, a.product_content, a.reg_date, a.category, a.subcategory , b.save_name , b.image_no FROM product a INNER JOIN product_image b ON a.product_no = b.product_no WHERE 1 = 1
AND category = '${category}'
ORDER BY a.product_no desc) c ) d WHERE d.rn > (#{pageNum}-1) * #{amount} AND d.rn <= #{pageNum} * #{amount}

SELECT d.product_no as productNo, d.product_name as productName, d.product_ea as productEa, d.price, d.product_content as productContent, d.reg_date as regDate, d.category, d.subcategory as subCategory, d.save_name as saveName, d.image_no as imageNo FROM(SELECT rownum as rn, c.product_no, c.product_name, c.product_ea, c.price, c.product_content, c.reg_date, c.category, c.subcategory , c.save_name , c.image_no FROM (SELECT a.product_no, a.product_name, a.product_ea, a.price, a.product_content, a.reg_date, a.category, a.subcategory , b.save_name , b.image_no FROM product a INNER JOIN product_image b ON a.product_no = b.product_no WHERE 1 = 1
AND a.category = '${category}'
AND a.subcategory = '${subCategory}' ORDER BY a.product_no DESC) c ) d WHERE d.rn > (#{pageNum}-1) * #{amount} AND d.rn <= #{pageNum} * #{amount}
