mapper.insert:

select seq_review_comment_no.nextval from dual 


	insert into review_comment 
		(review_comment_no
		,review_no
		,customer_no
		,content
		,reg_date
		)
	values 
		(
		#{commentNo}
		,#{reviewNo}
		,#{customerNo}
		,#{content}
		,sysdate
		)

mapper.update:

update review 
	set like_cnt = like_cnt + 1 
where review_no = #{reviewNo}
